
packet :: Int -> [Char] -> Int
packet cnt (a:z@(b:c:d:e)) | a == b = packet (cnt+1) z
                           | a == c = packet (cnt+1) z
                           | a == d = packet (cnt+1) z
                           | b == c = packet (cnt+1) z
                           | b == d = packet (cnt+1) z
                           | c == d = packet (cnt+1) z
                           | otherwise = cnt


input = ""


solution = packet 4 input
